INSERT INTO `page_parts` (`id`,`name`,`filter_id`,`content`,`page_id`) VALUES ('710','body','Textile','h2. <r:title />\r\n\r\n<div class=\"article-body\">\r\nRebecca Dallin is a preschool teacher and a closet geek.  She majored in English and had great aspirations of being a writer until she realized that to be a writer she would actually have to write - a lot.  Now she just blogs and writes for the amusement of friends and random strangers. In her spare time she watches old movies and quests for the perfect cup of hot chocolate.   She thinks she is very close to finding it.\r\n\r\n</div>\r\n\r\n<r:find url=\"/articles/\">\r\n<div id=\"articles\">\r\n  <ul>\r\n  <r:children:each order=\"desc\" by=\"published_at\">\r\n    <r:if_author name=\"rebecca\">\r\n    <li>\r\n      <r:link />\r\n        <p class=\"date\"><r:date format=\"%d. %B, %Y\" /> | <r:tags:each><r:tag_name /></r:tags:each> | <a href=\"/authors/<r:author_url />\"> by <r:author /></a></p>\r\n    </li>\r\n    </r:if_author>\r\n  </r:children:each>\r\n  </ul>\r\n</div>\r\n</r:find>','212');
